import json,datetime,boto3,traceback,time
from botocore.vendored import requests
from datetime import date, datetime
def json_serial(obj):
    if isinstance(obj, (datetime, date)):
        return obj.isoformat()
    raise TypeError ("Type %s not serializable" % type(obj))
def cfn_response(url, body):
    headers = {'content-type' : '','content-length' : str(len(body))}
    response = requests.put(url,data=body,headers=headers)
def acm_certificate(event, context):
    if event['RequestType'] in ['Create','Update']:
        cfn_response(event['ResponseURL'], _create_acm_certificate(event))
    else:
        cfn_response(event['ResponseURL'], _delete_acm_certificate(event))
def _create_acm_certificate(event):
    acm = boto3.client('acm')
    r53 = boto3.client('route53')
    ret = {}
    ret['StackId'] = event['StackId']
    ret['RequestId'] = event['RequestId']
    ret['LogicalResourceId'] = event['LogicalResourceId']
    rp = event['ResourceProperties']
    try:
        dn = rp['DomainName']
        clist = acm.list_certificates(CertificateStatuses=['PENDING_VALIDATION','ISSUED'])['CertificateSummaryList']
        cert_arn = None
        exists = ([c for c in clist if c['DomainName']== dn] or [None])[0]
        if exists != None:
            print("Cert exists")
            cert_arn = exists['CertificateArn']
        else:
            print("Cert does not exist. Creating one.")
            rcr = acm.request_certificate(
                DomainName=dn,
                ValidationMethod='DNS',
                IdempotencyToken=event['LogicalResourceId']
            )
            cert_arn = rcr['CertificateArn']
            time.sleep(10)
            dcr = acm.describe_certificate(
                CertificateArn=cert_arn
            )
            r53_c = []
            rr = dcr['Certificate']['DomainValidationOptions'][0]['ResourceRecord']
            bd = {'Comment':'Auth','Changes':[{'Action':'UPSERT','ResourceRecordSet':{'Name':rr['Name'],'Type':rr['Type'],'TTL': 3600,'ResourceRecords':[{'Value':rr['Value']}]}}]}
            r53.change_resource_record_sets(
                HostedZoneId=rp['HostedZoneId'],
                ChangeBatch=bd
            )
            allGood = False
            while not allGood:
                time.sleep(30)
                print("Checking if domain is validated.")
                cc = acm.describe_certificate(
                    CertificateArn=cert_arn
                )
                st = cc['Certificate']['Status']
                print("Curr Status %s" % st)
                if st == 'ISSUED':
                    allGood = True
            bd['Changes'][0]['Action'] = "DELETE"
            r53.change_resource_record_sets(
                HostedZoneId=rp['HostedZoneId'],
                ChangeBatch=bd
            )

        ret['PhysicalResourceId'] = cert_arn
        ret['Data'] = {}
        ret['Data']['CertificateArn'] = cert_arn
        ret['Status'] = 'SUCCESS'
    except:
        traceback.print_exc()
        ret['Status'] = 'FAILED'
        ret['Reason'] = 'Good question'
    finally:
        cfn_response(event['ResponseURL'], json.dumps(ret))
        return json.dumps(ret)

def _delete_acm_certificate(event):
    acm = boto3.client('acm')
    ret = {}
    ret['StackId'] = event['StackId']
    ret['RequestId'] = event['RequestId']
    ret['LogicalResourceId'] = event['LogicalResourceId']
    ret['PhysicalResourceId'] = event['PhysicalResourceId']
    try:
        response = acm.delete_certificate(
            CertificateArn=event['PhysicalResourceId']
        )
        print("got response %s" % json.dumps(response, default=json_serial))
        ret['Status'] = 'SUCCESS'
    except:
        traceback.print_exc()
        ret['Status'] = 'SUCCESS'
        ret['Reason'] = 'Good question'
    finally:
        cfn_response(event['ResponseURL'], json.dumps(ret))
        return json.dumps(ret)
